import React from 'react'


class Announcement extends React.Component{

    constructor(props){
        super(props)
        this.state = {annoucement: this.props.annoucement, showContent: false}
        this.toggleContent = this.toggleContent.bind(this)
    }


    toggleContent(){

        this.setState({...this.state, showContent: !showContent})
    }

    render(){

        let Content
        if(showContent){
            Content =  <p>{announcement.content}</p>
        }else{
            Content = <div></div>
        }
        return(

            <div onClick= {this.toggleContent}>
            <h4> {announcement.title}</h4>
            {Content}
            </div>

        )
    }


}
