import React from 'react'
import OrgCard from './OrgCard'



class RenderOrgCards extends React.Component{


    constructor(props){
        super(props)
        this.state = {orgs: this.props.orgs}
    }

    componentDidMount(){
        //get the orgId, name and location from the database and save them in an array
        fetch('http://localhost:4000/getBrowseOrgs',{method: 'GET'})
        .then(res => res.json())
        .then(data =>{
            this.setState({...this.state, orgs: data} )
            console.log(this.state.orgs)
        })
    }
   // this.state = {orgs: this.props.orgs}

    render(){


        console.log(this.state)
        return( 

        <div>
            {this.state.orgs.map((org) => (

                    <div key= {org.id}>
                        <OrgCard id={org.id} name={org.name} location= {org.location} />
                    </div>

            ))}
        </div>
        )
    } 
}

export default RenderOrgCards
