import React from 'react'
import { Redirect, Route } from 'react-router-dom'


const ProtectedRoute = ({component: Component, loggedIn, ...rest}) =>{

    fetch('http://localhost:4000/checkIfloggedIn', {

        method:'GET',
        origin: 'http:localhost:4000',
        credentials: 'include'

      })
        .then(res => res.json())
        .then(data => {
            if(data.user){
                this.setState({...this.state, loggedIn: data.loggedIn, user: data.user})
            }else{
              this.setState({...this.state, loggedIn: data.loggedIn})
            }
        } )
    return(

        <Route {...rest} render= {
            props =>{  
                if(loggedIn){
                   return <Component{...rest}{...props} />
                }
                else{
                    return <Redirect to={
                        {
                            pathname:'/login',
                            state:{
                                from: props.location
                            }
                        }
                    }/>
                }
        }
         } />
    )
}
export default ProtectedRoute;
